ptions['box_border_style']			='0';	$newoptions['box_border_color']			='#333333';	$newoptions['box_border_dim']			='1';	$newoptions['box_bg_color']				='#f7f7f7';	$newoptions['box_bg_color1']			='#f4f9ff';	$newoptions['outer_cellspacing']		='4';	$newoptions['outer_cellpadding']		='2';	$newoptions['inner_cellspacing']		='4';	$newoptions['inner_cellpadding']		='2';	$newoptions['cut_off']					='100';	$newoptions['use_resize']				='yes';	$newoptions['forumid']					='';	$newoptions['catid']					='';	$newoptions['user_can_choose']			='yes';	add_option('joomood_forums_options', $newoptions);}// XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX// add the admin pagefunction joomood_forums_add_pages() {	add_options_page('SE Forum Posts', 'SE Forum Posts', 8, __FILE__, 'joomood_forums_options');}// XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXfunction joomood_forums() {  $newoptions = get_option("joomood_forums_options");  	echo $before_widget;    echo $before_title;    echo "<h3>";    echo $newoptions['title'];        $title		 				= $newoptions['title'];    $numOfGroup 				= $newoptions['numOfGroup'];    $how_many_groups 			= $newoptions['how_many_groups'];    $image_border 				= $newoptions['image_border'];    $image_bordercolor 			= $newoptions['image_bordercolor'];    $go_profile_text 			= $newoptions['go_profile_text'];    $go_profile_text1 			= $newoptions['go_profile_text1'];    $go_profile_text2 			= $newoptions['go_profile_text2'];    $empty_image_url 			= $newoptions['empty_image_url'];    $pic_dim_width 				= $newoptions['pic_dim_width'];    $pic_dim_height 			= $newoptions['pic_dim_height'];    $nametype 					= $newoptions['nametype'];	$mainbox_width				= $newoptions['mainbox_width'];	$mainbox_border_style		= $newoptions['mainbox_border_style'];	$mainbox_border_color		= $newoptions['mainbox_border_color'];	$mainbox_border_dim			= $newoptions['mainbox_